import { useQuery, useMutation } from "@tanstack/react-query";
import { Helmet } from "react-helmet-async";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { LogOut, User, UserCheck, UserX, Users } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { User as UserType } from "@shared/schema";

export default function Dashboard() {
  const [, navigate] = useLocation();
  const { toast } = useToast();

  const { data: user, isLoading } = useQuery<UserType | null>({
    queryKey: ["/api/auth/me"],
  });

  const { data: pendingUsers = [], isLoading: isPendingLoading } = useQuery<UserType[]>({
    queryKey: ["/api/users/pending"],
    enabled: user?.role === "management",
  });

  const approvalMutation = useMutation({
    mutationFn: async ({ userId, status }: { userId: string; status: string }) => {
      const res = await apiRequest("PATCH", `/api/users/${userId}/approval`, { status });
      return await res.json();
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/users/pending"] });
      toast({
        title: "Success",
        description: `User ${variables.status === "approved" ? "approved" : "rejected"} successfully`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update user status",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/4"></div>
          <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
        </div>
      </div>
    );
  }

  if (!user) {
    navigate("/login");
    return null;
  }

  const handleLogout = async () => {
    try {
      await fetch("/api/auth/logout", {
        method: "POST",
        credentials: "include",
      });
      navigate("/login");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <>
      <Helmet>
        <title>Dashboard - Ramanujan Junior College</title>
      </Helmet>

      <div className="container mx-auto px-4 py-12">
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold mb-2" data-testid="dashboard-title">
              Welcome, {user.fullName}
            </h1>
            <p className="text-gray-600 dark:text-gray-400" data-testid="user-role">
              Role: <span className="capitalize font-medium">{user.role}</span>
            </p>
          </div>
          <Button onClick={handleLogout} variant="outline" data-testid="button-logout">
            <LogOut className="w-4 h-4 mr-2" />
            Logout
          </Button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card data-testid="card-profile">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="w-5 h-5" />
                Profile Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              <div>
                <span className="text-sm text-gray-600 dark:text-gray-400">Email:</span>
                <p className="font-medium" data-testid="user-email">{user.email || "Not provided"}</p>
              </div>
              <div>
                <span className="text-sm text-gray-600 dark:text-gray-400">Phone:</span>
                <p className="font-medium" data-testid="user-phone">{user.phoneNumber || "Not provided"}</p>
              </div>
              <div>
                <span className="text-sm text-gray-600 dark:text-gray-400">Status:</span>
                <p className="font-medium capitalize" data-testid="user-status">{user.approvalStatus}</p>
              </div>
            </CardContent>
          </Card>

          {user.approvalStatus === "pending" && (
            <Card className="bg-yellow-50 dark:bg-yellow-900/20 border-yellow-200 dark:border-yellow-800">
              <CardHeader>
                <CardTitle className="text-yellow-800 dark:text-yellow-400">
                  Approval Pending
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-sm text-yellow-700 dark:text-yellow-300" data-testid="pending-message">
                  Your account is awaiting approval from management. You will receive access once approved.
                </p>
              </CardContent>
            </Card>
          )}

          {user.role === "student" && user.studentId && (
            <Card className="bg-blue-50 dark:bg-blue-900/20 border-blue-200 dark:border-blue-800">
              <CardHeader>
                <CardTitle className="text-blue-800 dark:text-blue-400">
                  Student Information
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div>
                  <span className="text-sm text-blue-700 dark:text-blue-300">Student ID:</span>
                  <p className="font-bold text-lg text-blue-900 dark:text-blue-100" data-testid="student-id">
                    {user.studentId}
                  </p>
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {user.role === "management" && (
          <div className="mt-8">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Users className="w-5 h-5" />
                  Pending User Approvals
                </CardTitle>
              </CardHeader>
              <CardContent>
                {isPendingLoading ? (
                  <div className="animate-pulse space-y-3">
                    <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded"></div>
                    <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded"></div>
                  </div>
                ) : pendingUsers.length === 0 ? (
                  <p className="text-gray-500 dark:text-gray-400" data-testid="no-pending-users">
                    No pending approvals
                  </p>
                ) : (
                  <div className="space-y-4">
                    {pendingUsers.map((pendingUser) => (
                      <div
                        key={pendingUser.id}
                        className="flex items-center justify-between p-4 border border-gray-200 dark:border-gray-700 rounded-lg"
                        data-testid={`pending-user-${pendingUser.id}`}
                      >
                        <div className="flex-1">
                          <h3 className="font-semibold" data-testid={`text-name-${pendingUser.id}`}>
                            {pendingUser.fullName}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400" data-testid={`text-role-${pendingUser.id}`}>
                            Role: <span className="capitalize">{pendingUser.role}</span>
                          </p>
                          <p className="text-sm text-gray-600 dark:text-gray-400" data-testid={`text-phone-${pendingUser.id}`}>
                            Phone: {pendingUser.phoneNumber}
                          </p>
                          {pendingUser.studentId && (
                            <p className="text-sm text-gray-600 dark:text-gray-400" data-testid={`text-studentid-${pendingUser.id}`}>
                              Student ID: {pendingUser.studentId}
                            </p>
                          )}
                        </div>
                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            onClick={() => approvalMutation.mutate({ userId: pendingUser.id, status: "approved" })}
                            disabled={approvalMutation.isPending}
                            data-testid={`button-approve-${pendingUser.id}`}
                          >
                            <UserCheck className="w-4 h-4 mr-1" />
                            Approve
                          </Button>
                          <Button
                            size="sm"
                            variant="destructive"
                            onClick={() => approvalMutation.mutate({ userId: pendingUser.id, status: "rejected" })}
                            disabled={approvalMutation.isPending}
                            data-testid={`button-reject-${pendingUser.id}`}
                          >
                            <UserX className="w-4 h-4 mr-1" />
                            Reject
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </>
  );
}

client/src/pages/signup.tsx.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Helmet } from "react-helmet-async";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Link } from "wouter";
import { UserPlus } from "lucide-react";
import { registerUserSchema } from "@shared/schema";
import type { RegisterUser } from "@shared/schema";

type SignupForm = RegisterUser;

export default function Signup() {
  const [, navigate] = useLocation();
  const { toast } = useToast();

  const form = useForm<SignupForm>({
    resolver: zodResolver(registerUserSchema),
    defaultValues: {
      fullName: "",
      phoneNumber: "",
      email: "",
      password: "",
      role: "student",
      studentType: "present",
      existingStudentId: "",
      year: 1,
      section: "",
      rollNumber: "",
    },
  });

  const selectedRole = form.watch("role");
  const selectedStudentType = form.watch("studentType");

  const signupMutation = useMutation({
    mutationFn: async (data: SignupForm) => {
      const res = await apiRequest("POST", "/api/auth/register", data);
      return await res.json();
    },
    onSuccess: (response: any) => {
      toast({
        title: "Success",
        description: response.message || "Registration successful",
      });
      navigate("/login");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Registration failed",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: SignupForm) => {
    signupMutation.mutate(data);
  };

  return (
    <>
      <Helmet>
        <title>Sign Up - Ramanujan Junior College</title>
        <meta name="description" content="Create your Ramanujan Junior College account" />
      </Helmet>

      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 py-12 px-4 sm:px-6 lg:px-8">
        <Card className="w-full max-w-md">
          <CardHeader className="space-y-1">
            <div className="flex items-center justify-center mb-4">
              <UserPlus className="w-12 h-12 text-blue-600 dark:text-blue-400" />
            </div>
            <CardTitle className="text-2xl text-center" data-testid="signup-title">
              Create Account
            </CardTitle>
            <CardDescription className="text-center" data-testid="signup-subtitle">
              Register to access the college portal
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="fullName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Full Name *</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          placeholder="Enter your full name"
                          data-testid="input-fullname"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="phoneNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Phone Number *</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          placeholder="Enter 10-digit phone number"
                          data-testid="input-phone"
                          maxLength={10}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email (Optional)</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="email"
                          placeholder="Enter your email"
                          data-testid="input-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password *</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password"
                          placeholder="Create a password (min. 6 characters)"
                          data-testid="input-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>User Type *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-role">
                            <SelectValue placeholder="Select your user type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="student" data-testid="option-student">Student</SelectItem>
                          <SelectItem value="teacher" data-testid="option-teacher">Teacher</SelectItem>
                          <SelectItem value="management" data-testid="option-management">Management</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {selectedRole === "student" && (
                  <>
                    <FormField
                      control={form.control}
                      name="studentType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Student Status *</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-student-type">
                                <SelectValue placeholder="Are you a present or passed-out student?" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="present" data-testid="option-present">Present Student</SelectItem>
                              <SelectItem value="passed" data-testid="option-passed">Passed Out Student</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    {selectedStudentType === "passed" && (
                      <FormField
                        control={form.control}
                        name="existingStudentId"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Existing Student ID *</FormLabel>
                            <FormControl>
                              <Input 
                                {...field} 
                                placeholder="Enter your student ID"
                                data-testid="input-existing-studentid"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    )}

                    {selectedStudentType === "present" && (
                      <>
                        <FormField
                          control={form.control}
                          name="year"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Year *</FormLabel>
                              <Select onValueChange={(value) => field.onChange(parseInt(value))} defaultValue={field.value?.toString()}>
                                <FormControl>
                                  <SelectTrigger data-testid="select-year">
                                    <SelectValue placeholder="Select your year" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="1" data-testid="option-year-1">1st Year</SelectItem>
                                  <SelectItem value="2" data-testid="option-year-2">2nd Year</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="section"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Section *</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger data-testid="select-section">
                                    <SelectValue placeholder="Select your section" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="A" data-testid="option-section-a">Section A</SelectItem>
                                  <SelectItem value="B" data-testid="option-section-b">Section B</SelectItem>
                                  <SelectItem value="C" data-testid="option-section-c">Section C</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="rollNumber"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Roll Number *</FormLabel>
                              <FormControl>
                                <Input 
                                  {...field} 
                                  placeholder="Enter your roll number"
                                  data-testid="input-rollnumber"
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </>
                    )}
                  </>
                )}

                <p className="text-xs text-gray-500 dark:text-gray-400" data-testid="note-approval">
                  * Required fields. Note: Student and Teacher accounts require management approval before access.
                </p>

                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={signupMutation.isPending}
                  data-testid="button-signup"
                >
                  {signupMutation.isPending ? "Creating account..." : "Sign Up"}
                </Button>
              </form>
            </Form>

            <div className="mt-6 text-center text-sm">
              <span className="text-gray-600 dark:text-gray-400">
                Already have an account?{" "}
              </span>
              <Link href="/login" className="text-blue-600 hover:text-blue-700 dark:text-blue-400 dark:hover:text-blue-300 font-medium" data-testid="link-login">
                Login here
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    </>
  );
}

client/src/pages/payments.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Helmet } from "react-helmet-async";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { DollarSign, Upload, CheckCircle, Clock } from "lucide-react";
import type { StudentDue, Payment, User } from "@shared/schema";

const paymentSchema = z.object({
  dueId: z.string(),
  amount: z.string(),
  referenceId: z.string().min(1, "Payment reference ID is required"),
  screenshotUrl: z.string().url("Please enter a valid URL").optional(),
});

type PaymentForm = z.infer<typeof paymentSchema>;

export default function Payments() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedDue, setSelectedDue] = useState<StudentDue | null>(null);

  const { data: user } = useQuery<User>({
    queryKey: ["/api/auth/me"],
  });

  const { data: dues, isLoading: duesLoading } = useQuery<StudentDue[]>({
    queryKey: [`/api/dues/student/${user?.studentId}`],
    enabled: !!user?.studentId,
  });

  const { data: payments } = useQuery<Payment[]>({
    queryKey: [`/api/payments/student/${user?.studentId}`],
    enabled: !!user?.studentId,
  });

  const form = useForm<PaymentForm>({
    resolver: zodResolver(paymentSchema),
    defaultValues: {
      dueId: "",
      amount: "",
      referenceId: "",
      screenshotUrl: "",
    },
  });

  const paymentMutation = useMutation({
    mutationFn: async (data: PaymentForm) => {
      const res = await apiRequest("POST", "/api/payments", {
        ...data,
        studentId: user?.studentId,
      });
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Payment submitted for verification",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/dues/student/${user?.studentId}`] });
      queryClient.invalidateQueries({ queryKey: [`/api/payments/student/${user?.studentId}`] });
      setSelectedDue(null);
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit payment",
        variant: "destructive",
      });
    },
  });

  const handlePayDue = (due: StudentDue) => {
    setSelectedDue(due);
    form.setValue("dueId", due.id);
    form.setValue("amount", due.amount);
  };

  const onSubmit = (data: PaymentForm) => {
    paymentMutation.mutate(data);
  };

  const getStatusBadge = (status: string) => {
    const colors: { [key: string]: string } = {
      pending: "bg-yellow-500",
      paid: "bg-green-500",
      verified: "bg-blue-500",
    };
    return (
      <Badge className={`${colors[status] || "bg-gray-500"} text-white`}>
        {status}
      </Badge>
    );
  };

  if (duesLoading) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/3"></div>
          <div className="h-32 bg-gray-200 dark:bg-gray-700 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <>
      <Helmet>
        <title>Fee Payments - Ramanujan Junior College</title>
      </Helmet>

      <div className="bg-gradient-to-r from-blue-600 to-blue-800 text-white py-12 dark:from-blue-800 dark:to-blue-950">
        <div className="container mx-auto px-4">
          <h1 className="text-4xl font-bold mb-4" data-testid="page-title">Fee Payments</h1>
          <p className="text-xl opacity-90" data-testid="page-subtitle">
            View and manage your fee payments
          </p>
        </div>
      </div>

      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Pending Dues */}
          <div>
            <h2 className="text-2xl font-bold mb-6" data-testid="dues-title">Pending Dues</h2>

            {/* UPI Payment Info */}
            <Card className="mb-6 bg-blue-50 dark:bg-blue-900/20">
              <CardHeader>
                <CardTitle className="text-lg">Payment Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <div>
                  <p className="text-sm text-gray-600 dark:text-gray-400">UPI ID:</p>
                  <p className="font-mono font-bold" data-testid="upi-id">college@upi</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    Scan QR Code or use the UPI ID above to make payment
                  </p>
                </div>
              </CardContent>
            </Card>

            {!dues || dues.length === 0 ? (
              <Card>
                <CardContent className="py-12 text-center">
                  <CheckCircle className="w-12 h-12 text-green-500 mx-auto mb-4" />
                  <p className="text-gray-500 dark:text-gray-400" data-testid="no-dues">
                    No pending dues
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="space-y-4">
                {dues.map((due) => (
                  <Card key={due.id} data-testid={`due-card-${due.id}`}>
                    <CardContent className="py-4">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <h3 className="font-semibold" data-testid={`due-type-${due.id}`}>
                            {due.dueType}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400" data-testid={`due-desc-${due.id}`}>
                            {due.description}
                          </p>
                          <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                            Due Date: {new Date(due.dueDate).toLocaleDateString()}
                          </p>
                        </div>
                        <div className="text-right space-y-2">
                          <p className="text-2xl font-bold text-blue-600 dark:text-blue-400" data-testid={`due-amount-${due.id}`}>
                            ₹{due.amount}
                          </p>
                          {getStatusBadge(due.status)}
                          {due.status === "pending" && (
                            <Button
                              size="sm"
                              onClick={() => handlePayDue(due)}
                              data-testid={`button-pay-${due.id}`}
                            >
                              Pay Now
                            </Button>
                          )}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>

          {/* Payment Submission Form */}
          <div>
            <h2 className="text-2xl font-bold mb-6" data-testid="payment-form-title">
              Submit Payment
            </h2>

            {selectedDue ? (
              <Card>
                <CardHeader>
                  <CardTitle data-testid="selected-due-title">
                    {selectedDue.dueType}
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                      <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg">
                        <p className="text-sm text-gray-600 dark:text-gray-400">Amount to Pay:</p>
                        <p className="text-2xl font-bold" data-testid="payment-amount">
                          ₹{selectedDue.amount}
                        </p>
                      </div>

                      <FormField
                        control={form.control}
                        name="referenceId"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Payment Reference ID *</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                placeholder="Enter UPI transaction reference ID"
                                data-testid="input-reference"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="screenshotUrl"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Payment Screenshot URL (Optional)</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                placeholder="Upload screenshot and paste URL here"
                                data-testid="input-screenshot"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="flex gap-2">
                        <Button
                          type="submit"
                          className="flex-1"
                          disabled={paymentMutation.isPending}
                          data-testid="button-submit-payment"
                        >
                          <Upload className="w-4 h-4 mr-2" />
                          {paymentMutation.isPending ? "Submitting..." : "Submit Payment"}
                        </Button>
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setSelectedDue(null)}
                          data-testid="button-cancel"
                        >
                          Cancel
                        </Button>
                      </div>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="py-12 text-center">
                  <DollarSign className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500 dark:text-gray-400" data-testid="select-due-message">
                    Select a due from the left to make a payment
                  </p>
                </CardContent>
              </Card>
            )}

            {/* Payment History */}
            {payments && payments.length > 0 && (
              <div className="mt-8">
                <h3 className="text-xl font-bold mb-4" data-testid="payment-history-title">
                  Payment History
                </h3>
                <div className="space-y-3">
                  {payments.slice(0, 5).map((payment) => (
                    <Card key={payment.id} data-testid={`payment-${payment.id}`}>
                      <CardContent className="py-3">
                        <div className="flex items-center justify-between">
                          <div>
                            <p className="font-medium" data-testid={`payment-ref-${payment.id}`}>
                              Ref: {payment.referenceId}
                            </p>
                            <p className="text-sm text-gray-500 dark:text-gray-400">
                              ₹{payment.amount}
                            </p>
                          </div>
                          {getStatusBadge(payment.status)}
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

