import { useQuery } from "@tanstack/react-query";
import { Helmet } from "react-helmet-async";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Trophy, Star, Phone, Mail, Award } from "lucide-react";
import type { Faculty } from "@shared/schema";

export default function Teachers() {
  const { data: faculty, isLoading } = useQuery<Faculty[]>({
    queryKey: ["/api/faculty/ranked"],
  });

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="h-6 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded"></div>
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-5/6"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  const getRankBadgeColor = (rank: number) => {
    if (rank === 1) return "bg-yellow-500 text-white";
    if (rank === 2) return "bg-gray-400 text-white";
    if (rank === 3) return "bg-orange-600 text-white";
    return "bg-blue-500 text-white";
  };

  const getRankIcon = (rank: number) => {
    if (rank <= 3) return <Trophy className="w-4 h-4" />;
    return <Award className="w-4 h-4" />;
  };

  return (
    <>
      <Helmet>
        <title>Our Teachers - Ramanujan Junior College</title>
        <meta 
          name="description" 
          content="Meet our dedicated and highly qualified teaching staff at Ramanujan Junior College, ranked by student ratings." 
        />
      </Helmet>

      <div className="bg-gradient-to-r from-blue-600 to-blue-800 text-white py-12 dark:from-blue-800 dark:to-blue-950">
        <div className="container mx-auto px-4">
          <h1 className="text-4xl font-bold mb-4" data-testid="page-title">Our Teachers</h1>
          <p className="text-xl opacity-90" data-testid="page-subtitle">
            Excellence in Education
          </p>
        </div>
      </div>

      <div className="container mx-auto px-4 py-12">
        <div className="mb-8">
          <p className="text-gray-600 dark:text-gray-400" data-testid="info-text">
            Our teachers are ranked based on student ratings and feedback. The rankings reflect their dedication, teaching excellence, and student engagement.
          </p>
        </div>

        {!faculty || faculty.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center">
              <p className="text-gray-500 dark:text-gray-400" data-testid="no-teachers-text">
                No ranked teachers available yet.
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {faculty.map((teacher) => (
              <Card 
                key={teacher.id} 
                className="hover:shadow-lg transition-shadow duration-200"
                data-testid={`teacher-card-${teacher.id}`}
              >
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <CardTitle className="text-lg" data-testid={`teacher-name-${teacher.id}`}>
                      {teacher.name}
                    </CardTitle>
                    {teacher.rankPosition && (
                      <Badge 
                        className={`${getRankBadgeColor(teacher.rankPosition)} flex items-center gap-1`}
                        data-testid={`teacher-rank-${teacher.id}`}
                      >
                        {getRankIcon(teacher.rankPosition)}
                        Rank {teacher.rankPosition}
                      </Badge>
                    )}
                  </div>
                  <p className="text-sm text-gray-500 dark:text-gray-400" data-testid={`teacher-designation-${teacher.id}`}>
                    {teacher.designation}
                  </p>
                </CardHeader>
                <CardContent className="space-y-3">
                  {teacher.subject && (
                    <div className="flex items-center gap-2">
                      <Award className="w-4 h-4 text-blue-600 dark:text-blue-400" />
                      <span className="text-sm font-medium" data-testid={`teacher-subject-${teacher.id}`}>
                        {teacher.subject}
                      </span>
                    </div>
                  )}

                  {teacher.qualification && (
                    <div className="text-sm text-gray-600 dark:text-gray-400" data-testid={`teacher-qualification-${teacher.id}`}>
                      <strong>Qualification:</strong> {teacher.qualification}
                    </div>
                  )}

                  {teacher.experience && (
                    <div className="text-sm text-gray-600 dark:text-gray-400" data-testid={`teacher-experience-${teacher.id}`}>
                      <strong>Experience:</strong> {teacher.experience} years
                    </div>
                  )}

                  {teacher.averageRating && parseFloat(teacher.averageRating) > 0 && (
                    <div className="flex items-center gap-2 pt-2 border-t dark:border-gray-700">
                      <Star className="w-4 h-4 text-yellow-500 fill-yellow-500" />
                      <span className="text-sm font-semibold" data-testid={`teacher-rating-${teacher.id}`}>
                        Rating: {parseFloat(teacher.averageRating).toFixed(1)} / 5.0
                      </span>
                    </div>
                  )}

                  <div className="space-y-1 pt-2 border-t dark:border-gray-700">
                    {teacher.phone && (
                      <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
                        <Phone className="w-3 h-3" />
                        <span data-testid={`teacher-phone-${teacher.id}`}>{teacher.phone}</span>
                      </div>
                    )}
                    {teacher.email && (
                      <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
                        <Mail className="w-3 h-3" />
                        <span data-testid={`teacher-email-${teacher.id}`}>{teacher.email}</span>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </>
  );
}

client/src/pages/rate-teacher.tsx.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { Helmet } from "react-helmet-async";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Star, CheckCircle } from "lucide-react";
import type { RatingLink, Faculty } from "@shared/schema";

const ratingSchema = z.object({
  ratingValue: z.number().min(1).max(5),
  comment: z.string().optional(),
});

type RatingForm = z.infer<typeof ratingSchema>;

export default function RateTeacher() {
  const [, params] = useRoute("/rate/:token");
  const { toast } = useToast();
  const [submitted, setSubmitted] = useState(false);

  const { data: link, isLoading } = useQuery<RatingLink>({
    queryKey: [`/api/rating-links/${params?.token}`],
    enabled: !!params?.token,
  });

  const { data: faculty } = useQuery<Faculty>({
    queryKey: [`/api/faculty/${link?.facultyId}`],
    enabled: !!link?.facultyId,
  });

  const form = useForm<RatingForm>({
    resolver: zodResolver(ratingSchema),
    defaultValues: {
      ratingValue: 0,
      comment: "",
    },
  });

  const ratingMutation = useMutation({
    mutationFn: async (data: RatingForm) => {
      const res = await apiRequest("POST", "/api/ratings", {
        ...data,
        facultyId: link?.facultyId,
        ratingLinkId: link?.id,
      });
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Your rating has been submitted successfully!",
      });
      setSubmitted(true);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit rating",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-pulse text-center">
          <div className="h-8 w-64 bg-gray-200 dark:bg-gray-700 rounded mb-4 mx-auto"></div>
          <div className="h-4 w-48 bg-gray-200 dark:bg-gray-700 rounded mx-auto"></div>
        </div>
      </div>
    );
  }

  if (!link || !link.isActive) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 p-4">
        <Card className="max-w-md">
          <CardContent className="py-12 text-center">
            <p className="text-red-600 dark:text-red-400" data-testid="error-message">
              Invalid or inactive rating link.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (submitted) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 p-4">
        <Card className="max-w-md">
          <CardContent className="py-12 text-center space-y-4">
            <CheckCircle className="w-16 h-16 text-green-500 mx-auto" />
            <h2 className="text-2xl font-bold" data-testid="success-title">Thank You!</h2>
            <p className="text-gray-600 dark:text-gray-400" data-testid="success-message">
              Your rating has been submitted successfully. Your feedback helps us improve.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const onSubmit = (data: RatingForm) => {
    ratingMutation.mutate(data);
  };

  return (
    <>
      <Helmet>
        <title>Rate Teacher - Ramanujan Junior College</title>
      </Helmet>

      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 p-4">
        <Card className="w-full max-w-2xl">
          <CardHeader>
            <CardTitle className="text-2xl" data-testid="rating-title">
              Rate Teacher
            </CardTitle>
            <CardDescription data-testid="teacher-name">
              {faculty ? `${faculty.name} - ${faculty.subject || faculty.specialization}` : "Loading..."}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <FormField
                  control={form.control}
                  name="ratingValue"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Rating (1-5 stars) *</FormLabel>
                      <FormControl>
                        <RadioGroup
                          onValueChange={(value) => field.onChange(parseInt(value))}
                          className="flex gap-2"
                          data-testid="rating-stars"
                        >
                          {[1, 2, 3, 4, 5].map((value) => (
                            <div key={value} className="flex items-center space-x-1">
                              <RadioGroupItem value={value.toString()} id={`rating-${value}`} />
                              <Label htmlFor={`rating-${value}`} className="flex items-center cursor-pointer">
                                <Star
                                  className={`w-6 h-6 ${
                                    field.value >= value
                                      ? "text-yellow-500 fill-yellow-500"
                                      : "text-gray-300"
                                  }`}
                                />
                              </Label>
                            </div>
                          ))}
                        </RadioGroup>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="comment"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Comment (Optional)</FormLabel>
                      <FormControl>
                        <Textarea
                          {...field}
                          placeholder="Share your feedback about this teacher..."
                          rows={4}
                          data-testid="input-comment"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={ratingMutation.isPending}
                  data-testid="button-submit"
                >
                  {ratingMutation.isPending ? "Submitting..." : "Submit Rating"}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </>
  );
}

client/src/pages/login.tsx.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Helmet } from "react-helmet-async";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Link } from "wouter";
import { LogIn } from "lucide-react";
import { loginUserSchema } from "@shared/schema";
import type { LoginUser } from "@shared/schema";

type LoginForm = LoginUser;

export default function Login() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginUserSchema),
    defaultValues: {
      identifier: "",
      role: "student",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data: LoginForm) => {
      const res = await apiRequest("POST", "/api/auth/login", data);
      return await res.json();
    },
    onSuccess: async (response: any) => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      toast({
        title: "Success",
        description: response.message || "Login successful",
      });
      navigate("/dashboard");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Login failed",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: LoginForm) => {
    loginMutation.mutate(data);
  };

  return (
    <>
      <Helmet>
        <title>Login - Ramanujan Junior College</title>
        <meta name="description" content="Login to your Ramanujan Junior College account" />
      </Helmet>

      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 py-12 px-4 sm:px-6 lg:px-8">
        <Card className="w-full max-w-md">
          <CardHeader className="space-y-1">
            <div className="flex items-center justify-center mb-4">
              <LogIn className="w-12 h-12 text-blue-600 dark:text-blue-400" />
            </div>
            <CardTitle className="text-2xl text-center" data-testid="login-title">
              Welcome Back
            </CardTitle>
            <CardDescription className="text-center" data-testid="login-subtitle">
              Login to access your account
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="identifier"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Phone Number or Student ID</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          placeholder="Enter phone number or student ID"
                          data-testid="input-identifier"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>User Type</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-role">
                            <SelectValue placeholder="Select your user type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="student" data-testid="option-student">Student</SelectItem>
                          <SelectItem value="teacher" data-testid="option-teacher">Teacher</SelectItem>
                          <SelectItem value="management" data-testid="option-management">Management</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password" 
                          placeholder="Enter your password"
                          data-testid="input-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={loginMutation.isPending}
                  data-testid="button-login"
                >
                  {loginMutation.isPending ? "Logging in..." : "Login"}
                </Button>
              </form>
            </Form>

            <div className="mt-6 text-center text-sm">
              <span className="text-gray-600 dark:text-gray-400">
                Don't have an account?{" "}
              </span>
              <Link href="/signup" className="text-blue-600 hover:text-blue-700 dark:text-blue-400 dark:hover:text-blue-300 font-medium" data-testid="link-signup">
                Sign up here
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    </>
  );
}

scripts/import-staff.ts.content:
import { db } from "../server/db";
import { users } from "../shared/schema";
import * as fs from 'fs';
import * as path from 'path';
import bcrypt from 'bcryptjs';

async function importStaff() {
  try {
    const csvPath = path.join(process.cwd(), 'attached_assets', 'RJC_staff_directory_1760025214523.csv');
    const csvContent = fs.readFileSync(csvPath, 'utf-8');
    const lines = csvContent.trim().split('\n');

    // Skip header
    const dataLines = lines.slice(1);

    const staffToImport = [];

    for (const line of dataLines) {
      // Parse CSV line (handle commas in quotes)
      const values = line.match(/(".*?"|[^,]+)(?=\s*,|\s*$)/g) || [];
      const cleanValues = values.map(v => v.replace(/^"(.*)"$/, '$1').trim());

      const [department, name, qualifications, experienceYears, role, phone] = cleanValues;

      // Skip if no name
      if (!name) continue;

      // Determine actual role
      let userRole = 'teacher';
      if (role?.toLowerCase().includes('principal')) {
        userRole = 'principal';
      } else if (department === 'NON-TEACHING STAFF') {
        userRole = 'management';
      }

      // Generate username from name (lowercase, remove spaces and special chars)
      const username = name
        .toLowerCase()
        .replace(/[^a-z0-9]/g, '')
        .substring(0, 30);

      // For Principal Dilip Kumar Borah, use specified email
      let email = null;
      let password = 'teacher@123'; // default password

      if (name.includes('Dilip Kumar Borah')) {
        email = 'DilipKumarBorah@gmail.com';
        password = 'Education@13';
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(password, 10);

      staffToImport.push({
        username,
        password: hashedPassword,
        fullName: name,
        email,
        phoneNumber: phone || null,
        role: userRole,
        approvalStatus: 'approved', // Auto-approve all staff
        facultyId: null, // Will be linked later through faculty table
        studentId: null,
      });
    }

    // Insert all staff
    console.log(`Importing ${staffToImport.length} staff members...`);

    for (const staff of staffToImport) {
      try {
        await db.insert(users).values(staff);
        console.log(`✓ Imported: ${staff.fullName} (${staff.role})`);
      } catch (error: any) {
        if (error.code === '23505') { // Duplicate key error
          console.log(`⚠ Skipped (already exists): ${staff.fullName}`);
        } else {
          console.error(`✗ Error importing ${staff.fullName}:`, error.message);
        }
      }
    }

    console.log('\n✓ Staff import completed!');
    console.log('\nPrincipal login credentials:');
    console.log('Email: DilipKumarBorah@gmail.com');
    console.log('Password: Education@13');

  } catch (error) {
    console.error('Error importing staff:', error);
  } finally {
    process.exit(0);
  }
}

importStaff();
